  return (
    <ChatInputStyled>
      <div className="input-all">
        <form>
          <input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder={`Message #ROOM}`}
          />
          <Button hidden type="submit" onClick={sendMessage}>
            SEND
          </Button>
        </form>
      </div>
    </ChatInputStyled>
  );
}

export default ChatInput;
