import styled from "styled-components";

const ChatInputStyled = styled.div`

.input-all{
    border-radius: 20px;
    background-color: red;
}
form {
    position: relative;
    display: flex;
    justify-content: center;
}

 input {
    position: fixed;
    bottom: 30px;
    width: 10%;
    border: 1px solid gray;
    border-radius: 3px;
   
    outline: none;
}
> form > button {
    display: none;
}



`

export default ChatInputStyled;
