import React, {useState} from 'react'

//Material UI
import { Button } from "@material-ui/core";

// Import image
import { MaxImage } from "../../extra/max.png"

import ChatInputStyled  from './ChatInputStyled'
import {db} from "../../firebase";
import firebase from "firebase/compat/app";
;
function ChatInput(channelName, channelId) {

    const [input, setInput] = useState('');

    // preventdefault permet de rafraichir la page sans perdre les données
    const sendMessage = (e) => {
        e.preventDefault()
    };

    if (channelId) {
        return false;
    }

    db.collection('rooms').doc(channelId).collection("messages").add({
        message: input,
        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
        user: 'Maximilien R',
        userImage: {MaxImage},
    });

    return (

        <ChatInputStyled>
             <div className="input-all">
            <form>
            <input value={input} onChange={e => setInput(e.target.value)} placeholder= {`Message #ROOM}` }/>
            <Button hidden type='submit' onClick={sendMessage}>
                SEND
            </Button>
            </form>
            </div>
        </ChatInputStyled>
     
    )
}

export default ChatInput;
