import styled from 'styled-components';


const SideBarOptionStyled = styled.div `

        .sidebar-container {
            display:flex;
            align-items: center;
            font-size: 12px;
            padding-left:2px;
            cursor:pointer;
           

            :hover{
                opacity:0.9;
                background-color:#340e36;
            }

            > h3 {
                font-weight: 500;
            }

            >h3 >span {
                padding: 15px;
            }

            

           
        }
        
        .sidebaroptionchannel{
          padding:10px;
          width:auto;
          font-weight : 500;
          display:flex;
          justify-content: center;
          font-size: 12px;
          padding-left:2px;
          cursor:pointer;
        
        >.channels-option {
          display:flex;
          width: 100%;
          justify-content: center;
        }
        }`

export default SideBarOptionStyled;
