import styled from "styled-components";

const ChatStyled = styled.div`
  width: 100%;
  overflow-y: scroll;
  overflow: hidden;

  .header {
    display: flex;
    width: 98%;
    justify-content: space-between;
    padding: 20px;
    border-bottom: 1px solid lightgray;
  }

  .header-left {
      display:flex;
    > h4 {
      display: flex;
      width: 100%;
      text-transform: lowercase;
    }
    > h4 > .MuiSvgIcon-root {
      margin-left: 10px;
      font-size: 18px;
    }
  }

  .header-right {
    > p {
    display: flex;
    align-items: center;
    font-size: 14px;
  }
  > p > .MuiSvgIcon-root {
    margin-right: 5px !important;
    font-size: 16px;
  }
  }

  .chat-bottom {
    padding-bottom: 200px;

  }
`;

export default ChatStyled;
